import React, { Component } from 'react';
import './App.css';

// UI framework component imports
import Input from 'muicss/lib/react/input';
import Button from 'muicss/lib/react/button';
import Appbar from 'muicss/lib/react/appbar';

export default class HomeScreen extends Component {

  // Properties used by this component:
  // appActions, deviceInfo

  constructor(props) {
    super(props);

    this.state = {
    };
  }

  componentDidMount() {
  }

  componentWillUnmount() {
  }

  componentDidUpdate() {
  }

  textInputChanged_elField = (event) => {
    this.setState({field: event.target.value});
  }

  getValue_elField = () => {
    return this.state.field || '';
  }

  onClick_elButton = async () => {

  }


  onClick_elHotspot = async () => {
    // Go to screen 'Info'
    this.props.appActions.goToScreen('info', { transitionId: 'slideIn_right' });

  }


  render() {
    let layoutFlowStyle = {};
    let baseStyle = {};
    if (this.props.transitionId && this.props.transitionId.length > 0 && this.props.atTopOfScreenStack && this.props.transitionForward) {
      baseStyle.animation = '0.25s ease-in-out '+this.props.transitionId;
    }
    if ( !this.props.atTopOfScreenStack) {
      layoutFlowStyle.height = '100vh';
      layoutFlowStyle.overflow = 'hidden';
    }

    const style_elBackground = {
      width: '100%',
      height: '100%',
     };
    const style_elBackground_outer = {
      backgroundColor: '#f6f6f6',
     };
    const style_elText = {
      fontSize: 27.5,
      color: 'black',
      textAlign: 'center',
     };
    const style_elText2 = {
      fontSize: 21.8,
      color: 'rgba(0, 0, 0, 0.8500)',
      textAlign: 'center',
     };

    const style_elField = {
      display: 'block',
      paddingTop: 0,
      textAlign: 'left',
      pointerEvents: 'auto',
     };

    const style_elButton = {
      display: 'block',
      color: '(null)',
      textAlign: 'center',
      cursor: 'pointer',
      pointerEvents: 'auto',
     };

    const style_elHotspot = {
      display: 'block',
      textTransform: 'uppercase',
      backgroundColor: 'transparent',
      cursor: 'pointer',
      pointerEvents: 'auto',
     };
    const style_elText3 = {
      color: 'rgba(0, 0, 0, 0.8500)',
      textAlign: 'center',
     };
    // eslint-disable-next-line no-unused-vars
    const style_elText3_inner = {
      textAlign: 'center',
     };

    return (
      <div className="AppScreen HomeScreen" style={baseStyle}>
        <div className="background">
          <div className="containerMinHeight elBackground" style={style_elBackground_outer}>
            <div className="appBg" style={style_elBackground} />
          </div>
        </div>

        <div className="layoutFlow" style={layoutFlowStyle}>
          <div className="elSpacer">
            <div />
          </div>

          <div className="elText">
            <div className="systemFontBold" style={style_elText}>
              <div>{this.props.locStrings.start_text_502855}</div>
            </div>
          </div>

          <div className="elText2">
            <div className="systemFontRegular" style={style_elText2}>
              <div>{this.props.locStrings.home_text2_229629}</div>
            </div>
          </div>

          <div className="elField">
            <Input className="baseFont" style={style_elField} type="text" placeholder={this.props.locStrings.home_field_442316} onChange={this.textInputChanged_elField} value={this.getValue_elField()}  />
          </div>

          <div className="elButton">
            <Button className="actionFont" style={style_elButton}  color="accent" onClick={this.onClick_elButton} >
              {this.props.locStrings.home_button_234835}
            </Button>
          </div>
        </div>
        <Appbar className="navBar">
          <div className="title">Home</div>  <div className="backBtn" onClick={ (ev)=>{ this.props.appActions.goBack() } }></div>
        </Appbar>

        <div className="screenFgContainer">
          <div className="foreground">
            <div className="actionFont containerMinHeight elHotspot" style={style_elHotspot} onClick={this.onClick_elHotspot}  />
            <div className="baseFont elText3" style={style_elText3}>
              <div className="bottomAlignedContent">{this.props.locStrings.home_text3_554928}</div>
            </div>
          </div>
        </div>
      </div>
    )
  }

}
